import React from 'react'
import medium from '../../api/medium'
import '../../../src/App.css'
// import CenterBox from'../../components/CenterBox'

class MainPage extends React.Component{
    constructor(){
        super()
        this.state={
            infos: {}
        }
    }
    componentDidMount = async () => {
        const response = await medium.get('/info')
        this.setState({
            infos: response.data
        })
        console.log(this.state.infos)
    }

    render() {
        return(
            <div className="mainArea" style={{width: '100%!important'}}>
                <div className="ui container" style={{paddingTop: '150px'}}>
                    <div className="ui segment">
                        <h1 style={{textAlign: 'center'}}>Welcome to Space X!</h1>

                    <div className="description">
                        <h5>Some Important Facts</h5>

                    <div>
                        <ul>
                            {
                                Object.entries(this.state.infos).map(([key,value]) => {
                                    return(
                                        <div>
                                            {key.toString()} : {value.toString()}
                                        </div>
                                    )

                                })
                            }
                        </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        )
    }
}                                  

export default MainPage;
